mapper.select:
SELECT * FROM m_resboard WHERE r_uid NOT IN ( SELECT b_uid_1 FROM m_block WHERE b_uid_2 = #{u_id }
UNION DISTINCT
SELECT b_uid_2 FROM m_block WHERE b_uid_1 = #{u_id } )

mapper.update:
UPDATE m_resboard
SET r_sub = #{r_sub }, r_name = #{r_name }, r_address = #{r_address }, r_contents = #{r_contents }
WHERE r_id = #{r_id };

UPDATE m_resboard
SET r_view = r_view + 1
WHERE r_id = #{r_id };
