mapper.insert:
INSERT IGNORE
INTO m_restaurant(r_name, r_address)
VALUES(#{r_name }, #{r_address });

mapper.select:
SELECT *
FROM m_restaurant
WHERE r_name LIKE #{searchWord };

SELECT *
FROM m_restaurant
WHERE r_address LIKE #{searchWord };

SELECT *
FROM m_restaurant
ORDER BY r_name;

mapper.update:
UPDATE m_restaurant
SET r_name = #{r_name }, r_address = #{r_address }, r_mark = #{r_mark }
WHERE r_id = #{r_id };
